}

	render(){
		    return (
		    	<>
		    	<Options />
      <div 
		className={styles.Header}
		ref={ (divElement) => { this.divElement = divElement } }
		>
		      <p>{this.props.t('hello.label')}</p>
		      <p>{this.state.winScroll}</p>
		      <p>{this.state.height}</p>
      </div>
      </>
  );
	}

};


export default withTranslation()(Header);
