import React from "react";
import { useTranslation } from 'react-i18next'
import styles from "./Header.module.scss";


export default function Header(){
	const { t, i18n } = useTranslation();
    return (
      <div 
		className={styles.Header}>
		      <p>{t('hello.label')}</p>
      </div>
  );
};

